DROP TABLE IF EXISTS `cl_news`;
CREATE TABLE `cl_news` (
  `id` int(1) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `intro` varchar(255) DEFAULT NULL COMMENT '简介',
  `content` text NOT NULL COMMENT '内容',
  `publish_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '发布时间',
  `browse` int(11) NOT NULL DEFAULT '0' COMMENT '浏览量',
  `contributor` varchar(13) NOT NULL DEFAULT '' COMMENT '投稿者',
  `type` int(2) NOT NULL DEFAULT '0' COMMENT '一级类型',
  `sections` int(3) NOT NULL DEFAULT '0' COMMENT '二级分类',
  `state` int(11) DEFAULT NULL COMMENT '置顶状态',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `image_thumb` varchar(255) DEFAULT NULL COMMENT '缩略图',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=73 DEFAULT CHARSET=utf8 COMMENT='文章表';

