  `delete_tag` bit(1) NOT NULL DEFAULT b'0' COMMENT '删除标识 1为被删除 0为未被删除',
  PRIMARY KEY (`id`),
  KEY `topic_id` (`topic_id`)
) ENGINE=InnoDB AUTO_INCREMENT=493 DEFAULT CHARSET=utf8 COMMENT='话题评论表';

